async function loadProducts() {
  try {
    const response = await fetch('/api/products');
    const data = await response.json();
    const productsDiv = document.getElementById('products');
    productsDiv.innerHTML = '';
    
    data.products.forEach(product => {
      const productDiv = document.createElement('div');
      productDiv.classList.add('product');
      productDiv.innerHTML = `
        <strong>${product.name}</strong><br>
        ${product.price}¢<br>
        (${product.quantity} left)
      `;
      productDiv.onclick = () => selectProduct(product.id);
      productsDiv.appendChild(productDiv);
    });
    
    document.getElementById('balance').innerText = `Balance: ${data.balance}¢`;
  } catch (error) {
    displayMessage('Failed to load products', true);
  }
}

function displayMessage(message, isError = false) {
  const messagesDiv = document.getElementById('messages');
  messagesDiv.innerText = message;
  messagesDiv.className = isError ? 'error' : '';
  
  if (!isError) {
    setTimeout(() => {
      if (messagesDiv.innerText === message) {
        messagesDiv.innerText = 'INSERT COINS';
        messagesDiv.className = '';
      }
    }, 3000);
  }
}

async function insertCoin(coin) {
  try {
    const response = await fetch('/api/insert', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ coin })
    });
    const data = await response.json();
    if (data.success) {
      displayMessage(`Inserted ${coin}¢. Total: ${data.balance}¢`);
    } else {
      displayMessage('Invalid coin', true);
    }
    loadProducts();
  } catch (error) {
    displayMessage('Failed to insert coin', true);
  }
}

async function selectProduct(productId) {
  try {
    const response = await fetch('/api/select', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ productId })
    });
    const data = await response.json();
    
    displayMessage(data.message, !data.success);
    
    if (data.success) {
      const audio = new Audio('data:audio/wav;base64,');
      audio.volume = 0.2;
      audio.play();
      animateDispense(productId);
    }
    
    loadProducts();
  } catch (error) {
    displayMessage('Failed to select product', true);
  }
}

function createCoin(value, startX) {
  const coin = document.createElement('div');
  coin.className = 'coin';
  coin.style.left = `${startX}px`;
  coin.style.top = '50%';
  coin.textContent = value;
  
  const randomOffset = (Math.random() - 0.5) * 60;
  coin.style.left = `${startX + randomOffset}px`;
  
  const randomRotation = Math.random() * 90 - 45;
  coin.style.transform = `rotate(${randomRotation}deg)`;
  
  document.body.appendChild(coin);
  
  setTimeout(() => {
    document.body.removeChild(coin);
  }, 1500);
}

function animateRefund(amount) {
  const coins = [];
  let remaining = amount;
  const denominations = [100, 50, 25, 10, 5];
  
  denominations.forEach(value => {
    while (remaining >= value) {
      coins.push(value);
      remaining -= value;
    }
  });
  
  const refundButton = document.querySelector('.actions button');
  const rect = refundButton.getBoundingClientRect();
  const startX = rect.left + rect.width / 2;
  
  coins.forEach((value, index) => {
    setTimeout(() => {
      createCoin(value, startX);
      const audio = new Audio('data:audio/wav;base64,');
      audio.volume = 0.2;
      audio.play();
    }, index * 150);
  });
}

async function refund() {
  try {
    const response = await fetch('/api/refund', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' }
    });
    const data = await response.json();
    
    const currentBalance = parseInt(document.getElementById('balance').innerText.replace(/[^0-9]/g, ''));
    
    if (currentBalance > 0) {
      animateRefund(currentBalance);
    }
    
    displayMessage('Money refunded');
    loadProducts();
  } catch (error) {
    displayMessage('Failed to process refund', true);
  }
}

function animateDispense(productId) {
  const container = document.querySelector('.container');
  if (!container) return;
  
  const emojis = {
    'A1': '🥤',
    'B1': '🍟',
    'C1': '🍬'
  };
  
  const dispenseDiv = document.createElement('div');
  dispenseDiv.className = 'dispense-item';
  dispenseDiv.innerHTML = `<span>${emojis[productId] || '🎁'}</span><br>Enjoy!`;
  
  const rect = container.getBoundingClientRect();
  dispenseDiv.style.left = '50%';
  dispenseDiv.style.top = '40%';
  dispenseDiv.style.transform = 'translate(-50%, -50%)';
  
  container.appendChild(dispenseDiv);
  
  setTimeout(() => {
    container.removeChild(dispenseDiv);
  }, 3000);
}

async function restockProducts() {
  try {
    const response = await fetch('/api/restock', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' }
    });
    const data = await response.json();
    
    if (data.success) {
      displayMessage('Products restocked! 📦');
      animateRestock();
    }
    
    loadProducts();
  } catch (error) {
    displayMessage('Failed to restock products', true);
  }
}

function animateRestock() {
  const container = document.querySelector('.container');
  if (!container) return;
  
  const items = ['🥤', '🍟', '🍬'];
  
  items.forEach((emoji, index) => {
    setTimeout(() => {
      const restockDiv = document.createElement('div');
      restockDiv.className = 'restock-item';
      restockDiv.innerHTML = `<span>${emoji}</span>`;
      
      const rect = container.getBoundingClientRect();
      const randomX = Math.random() * (rect.width - 100);
      restockDiv.style.left = `${randomX}px`;
      
      container.appendChild(restockDiv);
      
      setTimeout(() => {
        container.removeChild(restockDiv);
      }, 1500);
    }, index * 200);
  });
}

document.addEventListener('DOMContentLoaded', () => {
  loadProducts();
  displayMessage('INSERT COINS');
});
